import React, { useState } from 'react';
import axios from 'axios';
import store from '../../store';
import TweetsList from '../TweetsList';
import './style.css';


const Result : React.FC = () => {
    const [num, setNum] = useState<number>()
    const [all, setAll] = useState<number>()
    axios.get('http://127.0.0.1:5000/all').then(response => setAll(response.data))

    axios.get('http://127.0.0.1:5000/test')
        .then(response => setNum(response.data))
    let per = num!/all! * 100
    return (
        <div className="results">  
            <div className="tweets">
                <TweetsList />
            </div>
                <h2 className="howmany">There are <b>{num}</b>  tweets that contain bad words</h2>
            <h3 className="percent">{per}% tweets have bad words</h3> 
        </div>
    )
}

export default Result;
