import React, { FC, Props, useState, VFC } from 'react';
import axios from 'axios';

const TweetsList: FC = () => {
    const [tweetsList, setTweetsList] = useState([])
    let tweets: string[] = []
    const get_tweets = async () => {
        await axios.post(`http://localhost:5000/get_tweets/`)
        .then(response => {tweets.push(response.data)})
        const listItems = tweets.map((tw, index) =>
        <li key={index}>
            {tw}
        </li>
        )
        setTweetsList(tweetsList)
        return (
            <ul>
                {tweetsList}
            </ul>
        )
    }
}

export default TweetsList
